// Хук для соединение с сервером, получения ошибок и авторизации
// 1. Принимает на вход api url
// 2. Посылает запрос с информацией из 'doFetch'
// 3. Обрабатывает ответ

import { useState, useEffect } from 'react';
import axios from 'axios';

export default (url) => {
  const baseUrl = 'https://conduit.productionready.io/api';

  const [isLoading, setIsLoading] = useState(false);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  // Создаем 'state' для передачи 'options' из 'doFetch' в 'axios'
  const [options, setOptions] = useState({});
  
  // 'options' содержат информацию с полей ввода
  const doFetch = (options = {}) => {
  	setOptions(options);
  	setIsLoading(true);
  }

	useEffect(() => {
		if(!isLoading) {
			return
		}

		axios(baseUrl + url, options)
		.then((res) => {
			console.log(`Succes: ${res}`);
			setIsLoading(false);
			setResponse(res.data);
		})
		.catch((err) => {
			console.log(err)
			setIsLoading(false);
			setError(err.response.data);
		})
	}, [isLoading]);
  
  return [ {isLoading, response, error}, doFetch ];
}