import {parse} from 'query-string';

export const range = (start, end) => {
  return [...Array(end).keys()].map(el => el + start)
}

export const limit = 10;

// Для передачи на сервер параметров (offset & limit)
// Данные параметры позволяют серверу понять,
// какую страницу мы запрашиваем основываясь на offset (offset 0 - 1 страница; 10 - 2; 20 - 3)
export const getPaginator = (search) => {
	// Получаем объект {page: pageNumber} из параметра search
	const parsedSearch = parse(search);
	const currentPage = parsedSearch.page ? Number(parsedSearch.page) : 1;
	// Формула расчета offset
	const offset = currentPage * 10 - limit;
	return {currentPage, offset};
}